import {
  CarouselLayout,
  ControlBar,
  DisconnectButton,
  ParticipantTile,
  RoomAudioRenderer,
  useRoomContext,
  useTracks,
} from "@livekit/components-react";
import axios from "axios";
import {
  DataPacket_Kind,
  RemoteParticipant,
  RoomEvent,
  Track,
} from "livekit-client";
import React, { useCallback, useEffect, useState } from "react";

function SendMessage({
  mode,
  messages,
  setMessages,
}: {
  mode: string;
  messages: any[];
  setMessages: React.Dispatch<React.SetStateAction<any[]>>;
}) {
  const room = useRoomContext();
  const tracks = useTracks(
    [
      { source: Track.Source.Camera, withPlaceholder: true },
      { source: Track.Source.ScreenShare, withPlaceholder: false },
    ],
    { onlySubscribed: false }
  );

  const handleDataReceived = useCallback(
    (
      payload: Uint8Array,
      participant?: RemoteParticipant,
      kind?: DataPacket_Kind,
      topic?: string
    ) => {
      const decoder = new TextDecoder();
      const decodedMessage = decoder.decode(payload);
      console.log("Message received:", decodedMessage);
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: participant?.identity || "Unknown", content: decodedMessage },
      ]);
    },
    [setMessages]
  );

  useEffect(() => {
    room.on(RoomEvent.DataReceived, handleDataReceived);
    return () => {
      room.off(RoomEvent.DataReceived, handleDataReceived);
    };
  }, [handleDataReceived, room]);

  const sendMessage = async (message: string) => {
    if (!room) return;

    const encoder = new TextEncoder();
    const encodedMessage = encoder.encode(message);

    try {
      await room.localParticipant.sendData(
        encodedMessage,
        DataPacket_Kind.RELIABLE
      );
      console.log("Message sent:", message);
    } catch (error) {
      console.error("Error sending message:", error);
    }
  };

  return (
    room && (
      <div>
        <div className="lk-control-bar">
          <ControlBar
            variation="verbose"
            controls={{ screenShare: false, leave: false }}
          />
          <DisconnectButton
            stopTracks={true}
            onClick={() => console.log("Leave")}
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="16"
              height="16"
              fill="none"
            >
              <path
                fill="currentColor"
                fillRule="evenodd"
                d="M2 2.75A2.75 2.75 0 0 1 4.75 0h6.5A2.75 2.75 0 0 1 14 2.75v10.5A2.75 2.75 0 0 1 11.25 16h-6.5A2.75 2.75 0 0 1 2 13.25v-.5a.75.75 0 0 1 1.5 0v.5c0 .69.56 1.25 1.25 1.25h6.5c.69 0 1.25-.56 1.25-1.25V2.75c0-.69-.56-1.25-1.25-1.25h-6.5c-.69 0-1.25.56-1.25 1.25v.5a.75.75 0 0 1-1.5 0v-.5Z"
                clipRule="evenodd"
              ></path>
              <path
                fill="currentColor"
                fillRule="evenodd"
                d="M8.78 7.47a.75.75 0 0 1 0 1.06l-2.25 2.25a.75.75 0 1 1-1.06-1.06l.97-.97H1.75a.75.75 0 0 1 0-1.5h4.69l-.97-.97a.75.75 0 0 1 1.06-1.06l2.25 2.25Z"
                clipRule="evenodd"
              ></path>
            </svg>{" "}
            Leave
          </DisconnectButton>
        </div>
        {mode === "video" && (
          <CarouselLayout
            tracks={tracks}
            style={{ height: "calc(100vh - var(--lk-control-bar-height))" }}
          >
            <ParticipantTile />
          </CarouselLayout>
        )}
        {mode === "audio" && <RoomAudioRenderer />}
      </div>
    )
  );
}
export default SendMessage;
