import {
    LiveKitRoom
  } from "@livekit/components-react";
  import "@livekit/components-styles";
  import axios from "axios";
  import React, { useEffect, useState } from "react";
import SendMessage from "./sendMessage";

  // const serverUrl = "wss://randomroom-f0bn812z.livekit.cloud";
  const serverUrl = "ws://192.168.0.41:7880";

  function ChatBot({ roomName, participantName }:any) {
    const [token, setToken] = useState(null);
    // const room = RoomContext
    // const roomRef = useRef < Room > new Room();
    const [messageInput, setMessageInput] = useState("");

    useEffect(() => {
      const fetchToken = async () => {
        try {
          const response = await axios.post(
            "http://192.168.0.41:3002/livekit/tokenGenerate",
            {
              roomName,
              participantName,
            }
          );
          if (response.data.token) {
            console.log("Token received:", response.data.token);
            setToken(response.data.token);
          } else {
            console.error("Token response did not contain a token:", response.data);
          }
        } catch (error) {
          console.error("Error fetching token:", error);
        }
      };

      fetchToken();
    }, [roomName, participantName]);


    // useEffect(() => {
    //   if (room) {
    //     room.on("dataReceived", (payload, participant) => {
    //       const newMessage = {
    //         sender: participant.identity,
    //         content: new TextDecoder().decode(payload),
    //       };
    //       setMessages((prevMessages) => [...prevMessages, newMessage]);
    //     });
    //   }
    // }, [room]);

    // const handleSendMessage = () => {
    //   console.log(room);
    //   if (room && messageInput.trim()) {
    //     try {
    //       room.localParticipant.publishData(messageInput, "message");
    //       setMessages((prevMessages) => [
    //         ...prevMessages,
    //         { sender: participantName, content: messageInput },
    //       ]);
    //       setMessageInput("");
    //     } catch (error) {
    //       console.error("Error sending message:", error);
    //     }
    //   }
    // };

    if (!token) {
      return <div>Loading...</div>;
    }

    return (
      <div style={{ display: "flex", height: "100vh", flexDirection: "column" }}>
        {token &&
         <LiveKitRoom
          video={false}
          audio={false}
          token={token}
          connect={true}
          serverUrl={serverUrl}
          data-lk-theme="default"
          style={{ flex: 1 }}
        >
          <SendMessage />
        </LiveKitRoom>}
        <div style={{ padding: "10px", borderTop: "1px solid #ccc" }}>
          <div
            style={{ height: "200px", overflowY: "scroll", marginBottom: "10px" }}
          >
            {/* {messages.map((message, index) => (
              <div key={index}>
                <strong>{message.sender}:</strong> {message.content}
              </div>
            ))} */}
          </div>
          <div style={{ display: "flex" }}>
            <input
              type="text"
              value={messageInput}
              onChange={(e) => setMessageInput(e.target.value)}
              style={{ flex: 1 }}
            />
            {/* <button onClick={handleSendMessage}>Send</button> */}
          </div>
        </div>
      </div>
    );
  }

  export default ChatBot;
